targets.expr:
sum_over_time(
(

# all instances are idle
(
# idle workers / connected workers
dask_scheduler_workers{cluster_id="$cluster_id",state="idle"}
/ on () sum(dask_scheduler_workers{cluster_id="$cluster_id",state!="connected"})
# only trust metric when scheduler is running
and on () sum(rate(node_cpu_seconds_total{cluster_id="$cluster_id", spec="scheduler"}[$__rate_interval])) > 0
) == 1

)[12h:1s]
)

sum_over_time(
(
    count(
        # average CPU over entire cluster < 20%
        avg(sum by(instance,cpu) (irate(node_cpu_seconds_total{cluster_id="$cluster_id", spec="worker", mode!="idle"}[$__rate_interval]))) < .1
        # when tasks were processing
        and on () dask_scheduler_tasks{cluster_id="$cluster_id",state="processing"} > 0
    )
)[12h:1s]
)

sum_over_time(    
    count(
        # for at least 1 worker
        (
            # number of instances with
            count(
                # 80% or higher mem util (i.e., less than 20% available)
                (node_memory_MemAvailable_bytes{cluster_id="$cluster_id", spec="worker"} / node_memory_MemTotal_bytes{cluster_id="$cluster_id", spec="worker"}) < .2
            )
        ) > 1
    )[12h:1s]
)

sum_over_time(
(
    # at least one instance with recv rate over 10MiB/s
    count(
        avg (
            rate(node_network_receive_bytes_total{cluster_id="$cluster_id", spec="worker"}[$__rate_interval])
        ) > 10000000
    )
    # while there are tasks processing
    and on () dask_scheduler_tasks{cluster_id="$cluster_id",state="processing"} > 0

)[12h:1s]
)

count_over_time(
(

  # any workers aren't reporting dask metrics
  (
    count(
      sum by (instance)(rate(node_cpu_seconds_total{cluster_id="$cluster_id",spec="worker"}[$__rate_interval]) > 0)
    )
    - count(dask_worker_threads{cluster_id="$cluster_id"})
  ) > 0
  # only trust metric when scheduler is running
  and on () sum(rate(node_cpu_seconds_total{cluster_id="$cluster_id", spec="scheduler"}[$__rate_interval])) > 0

)[12h:1s]
)

dask_scheduler_workers{cluster_id="$cluster_id",state!="connected"}
# only trust metric when scheduler is running
and on () sum(rate(node_cpu_seconds_total{cluster_id="$cluster_id", spec="scheduler"}[$__rate_interval])) > 0

  (
    node_memory_MemTotal_bytes{cluster_id="$cluster_id", spec="scheduler"}
    - node_memory_MemAvailable_bytes{cluster_id="$cluster_id", spec="scheduler"}
  ) / node_memory_MemTotal_bytes{cluster_id="$cluster_id", spec="scheduler"}
# only trust metric when scheduler is running
and on () sum(rate(node_cpu_seconds_total{cluster_id="$cluster_id", spec="scheduler"}[$__rate_interval])) > 0

node_network_transmit_bytes_total{cluster_id="$cluster_id", spec="scheduler"}
# only trust metric when scheduler is running
and on () sum(rate(node_cpu_seconds_total{cluster_id="$cluster_id", spec="scheduler"}[$__rate_interval])) > 0

min(
    node_memory_MemTotal_bytes{cluster_id="$cluster_id", spec="worker"}
    # only trust metric when scheduler is running
    and on () rate(node_cpu_seconds_total{cluster_id="$cluster_id", spec="scheduler"}[$__rate_interval]) > 0
)

min(
    node_memory_MemTotal_bytes{cluster_id="$cluster_id", spec="worker"}
    # only trust metric when scheduler is running
    and on () rate(node_cpu_seconds_total{cluster_id="$cluster_id", spec="scheduler"}[$__rate_interval]) > 0
) * 0.6
